import React, { Component } from "react";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
// import AuthService from "../services/auth.service";
// import { PostData } from "./PostData";
import { Redirect } from 'react-router-dom';


const required = value => {
    if (!value) {
        return (
            <div className="alert alert-danger" role="alert">
                This field is required!
            </div>
        );
    }
};

export default class Login extends Component {

    constructor(props) {
        super(props);
        this.handleLogin = this.handleLogin.bind(this);
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);

        this.state = {
          username: "",
          password: "",
          loading: false,
          message: ""
        };
      }

      onChangeUsername(e) {
        this.setState({
          username: e.target.value
        });
      }

      onChangePassword(e) {
        this.setState({
          password: e.target.value
        });
      }

      handleLogin(e) {
        e.preventDefault();

        this.setState({
          message: "",
          loading: true
        });

        this.form.validateAll();




    }


    render() {

        const {loading} = this.state;

        // if (this.state.redirectToReferrer) {
        //     return (<Redirect to={'/dashboard'} />)
        // }

        // if (sessionStorage.getItem('token')) {
        //     return (<Redirect to={'/dashboard'} />)
        // }

        return (

<div className="d-flex flex-column flex-root">
  {/*begin::Login*/}
  <div className="login login-1 login-signin-on d-flex flex-column flex-lg-row flex-column-fluid bg-white" id="kt_login">
    {/*begin::Aside*/}
    <div className="login-aside d-flex flex-row-auto bgi-size-cover bgi-no-repeat p-10 p-lg-10" style={{backgroundImage: 'url(assets/media/bg/bg-4.jpg)'}}>
      {/*begin: Aside Container*/}
      <div className="d-flex flex-row-fluid flex-column justify-content-between">
        {/*begin: Aside header*/}
        <a href="#" className="flex-column-auto mt-5 pb-lg-0 pb-10">
          <img src="assets/media/logos/logo-letter-1.png" className="max-h-70px" alt />
        </a>
        {/*end: Aside header*/}
        {/*begin: Aside content*/}
        <div className="flex-column-fluid d-flex flex-column justify-content-center">
          <h3 className="font-size-h1 mb-5 text-white">Welcome to Metronic!</h3>
          <p className="font-weight-lighter text-white opacity-80">The ultimate Bootstrap, Angular 8, React &amp; VueJS admin theme framework for next generation web apps.</p>
        </div>
        {/*end: Aside content*/}
        {/*begin: Aside footer for desktop*/}
        <div className="d-none flex-column-auto d-lg-flex justify-content-between mt-10">
          <div className="opacity-70 font-weight-bold text-white">© 2020 Metronic</div>
          <div className="d-flex">
            <a href="#" className="text-white">Privacy</a>
            <a href="#" className="text-white ml-10">Legal</a>
            <a href="#" className="text-white ml-10">Contact</a>
          </div>
        </div>
        {/*end: Aside footer for desktop*/}
      </div>
      {/*end: Aside Container*/}
    </div>
    {/*begin::Aside*/}
    {/*begin::Content*/}
    <div className="d-flex flex-column flex-row-fluid position-relative p-7 overflow-hidden">
      {/*begin::Content header*/}
