import React, { Component } from "react";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
// import AuthService from "../services/auth.service";
// import { PostData } from "./PostData";
import { Redirect } from 'react-router-dom';


const required = value => {
    if (!value) {
        return (
            <div className="alert alert-danger" role="alert">
                This field is required!
            </div>
        );
    }
};

export default class Login extends Component {

    constructor(props) {
        super(props);
        this.handleLogin = this.handleLogin.bind(this);
        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);

        this.state = {
          username: "",
          password: "",
          loading: false,
          message: ""
        };
      }

      onChangeUsername(e) {
        this.setState({
          username: e.target.value
        });
      }

      onChangePassword(e) {
        this.setState({
          password: e.target.value
        });
      }

      handleLogin(e) {
        e.preventDefault();

        this.setState({
          message: "",
          loading: true
        });

        this.form.validateAll();




    }


    render() {

        const {loading} = this.state;

        // if (this.state.redirectToReferrer) {
        //     return (<Redirect to={'/dashboard'} />)
        // }

        // if (sessionStorage.getItem('token')) {
        //     return (<Redirect to={'/dashboard'} />)
        // }

        return (

<div className="d-flex flex-column flex-root">
  {/*begin::Login*/}
  <div className="login login-2 login-signin-on d-flex flex-column flex-lg-row flex-column-fluid bg-white" id="kt_login">
    {/*begin::Aside*/}
    <div className="login-aside order-2 order-lg-1 d-flex flex-row-auto position-relative overflow-hidden">
