import axios from "axios";

    let username = 'admin';
    let password = 'Pass@1234';
    const token = Buffer.from(`${username}:${password}`, 'utf8').toString('base64')


class AuthLogin {

  login(username, password) {
    return axios.post('https://ecological.chinikiguard.com/accounts/api/jwt/' , { username,  password },  { headers: { 'Authorization': `Basic ${token}`,'Content-Type': 'multipart/form-data',
    'Access-Control-Allow-Origin': '*',
    'Access-Control-Allow-Methods': 'GET,HEAD,OPTIONS',
    'Access-Control-Allow-Credentials': true }, }   )
      .then(response => {
        if (response.data.token) {
          localStorage.setItem("user", JSON.stringify(response.data));
        }

        return response.data;
      });
  }



  logout() {
    localStorage.removeItem("user");
  }


  getCurrentUser() {
    return JSON.parse(localStorage.getItem('user'));
  }
}

export default new AuthLogin();
